import{_ as o,c as a,o as i,a6 as t}from"./chunks/framework.DFafSIgU.js";const p=JSON.parse('{"title":"Privacy Policy","description":"Privacy Policy for PFinalClub - Learn how we collect, use, and protect your personal information.","frontmatter":{"title":"Privacy Policy","description":"Privacy Policy for PFinalClub - Learn how we collect, use, and protect your personal information.","keywords":"privacy policy, data protection, cookies, GDPR, personal information","date":"2025-03-14T00:00:00.000Z","author":"PFinalClub","hidden":true,"head":[["meta",{"name":"keywords","content":"privacy policy, data protection, cookies, GDPR, personal information"}]]},"headers":[],"relativePath":"privacy-policy.md","filePath":"privacy-policy.md","lastUpdated":1762769214000}'),r={name:"privacy-policy.md"};function n(l,e,s,c,d,u){return i(),a("div",{"data-pagefind-body":!0},e[0]||(e[0]=[t('<h1 id="privacy-policy" tabindex="-1">Privacy Policy <a class="header-anchor" href="#privacy-policy" aria-label="Permalink to &quot;Privacy Policy&quot;">​</a></h1><p><strong>Last Updated: November 10, 2025</strong></p><p>PFinalClub (&quot;we,&quot; &quot;our,&quot; or &quot;us&quot;) is committed to protecting your privacy. This Privacy Policy explains how we collect, use, disclose, and safeguard your information when you visit our website <a href="https://friday-go.icu" target="_blank" rel="noreferrer">https://friday-go.icu</a>.</p><h2 id="_1-information-we-collect" tabindex="-1">1. Information We Collect <a class="header-anchor" href="#_1-information-we-collect" aria-label="Permalink to &quot;1. Information We Collect&quot;">​</a></h2><h3 id="_1-1-personal-information" tabindex="-1">1.1 Personal Information <a class="header-anchor" href="#_1-1-personal-information" aria-label="Permalink to &quot;1.1 Personal Information&quot;">​</a></h3><p>We may collect personal information that you voluntarily provide to us when you:</p><ul><li>Subscribe to our newsletter</li><li>Leave comments on articles</li><li>Contact us through our contact form</li><li>Create an account (if applicable)</li></ul><p>This information may include:</p><ul><li>Name</li><li>Email address</li><li>IP address</li><li>Location data (city/country level)</li></ul><h3 id="_1-2-automatically-collected-information" tabindex="-1">1.2 Automatically Collected Information <a class="header-anchor" href="#_1-2-automatically-collected-information" aria-label="Permalink to &quot;1.2 Automatically Collected Information&quot;">​</a></h3><p>When you visit our website, we automatically collect certain information about your device, including:</p><ul><li>Browser type and version</li><li>Operating system</li><li>Device type (desktop, mobile, tablet)</li><li>Referring/exit pages</li><li>Date and time stamps</li><li>Pages viewed and time spent on pages</li></ul><h3 id="_1-3-cookies-and-tracking-technologies" tabindex="-1">1.3 Cookies and Tracking Technologies <a class="header-anchor" href="#_1-3-cookies-and-tracking-technologies" aria-label="Permalink to &quot;1.3 Cookies and Tracking Technologies&quot;">​</a></h3><p>We use cookies and similar tracking technologies to:</p><ul><li>Remember your preferences</li><li>Understand how you use our website</li><li>Improve our content and services</li><li>Deliver personalized advertisements</li></ul><p><strong>Types of Cookies We Use:</strong></p><ul><li><strong>Essential Cookies</strong>: Required for the website to function properly</li><li><strong>Analytics Cookies</strong>: Help us understand how visitors interact with our website</li><li><strong>Advertising Cookies</strong>: Used to deliver relevant advertisements</li></ul><h2 id="_2-how-we-use-your-information" tabindex="-1">2. How We Use Your Information <a class="header-anchor" href="#_2-how-we-use-your-information" aria-label="Permalink to &quot;2. How We Use Your Information&quot;">​</a></h2><p>We use the collected information for:</p><ul><li><strong>Service Delivery</strong>: Providing and maintaining our website and services</li><li><strong>Communication</strong>: Responding to your inquiries and sending newsletters</li><li><strong>Analytics</strong>: Understanding usage patterns and improving user experience</li><li><strong>Advertising</strong>: Displaying relevant advertisements through Google AdSense</li><li><strong>Security</strong>: Detecting and preventing fraud or malicious activities</li><li><strong>Legal Compliance</strong>: Complying with applicable laws and regulations</li></ul><h2 id="_3-google-adsense-and-third-party-advertising" tabindex="-1">3. Google AdSense and Third-Party Advertising <a class="header-anchor" href="#_3-google-adsense-and-third-party-advertising" aria-label="Permalink to &quot;3. Google AdSense and Third-Party Advertising&quot;">​</a></h2><h3 id="_3-1-google-adsense" tabindex="-1">3.1 Google AdSense <a class="header-anchor" href="#_3-1-google-adsense" aria-label="Permalink to &quot;3.1 Google AdSense&quot;">​</a></h3><p>We use Google AdSense to display advertisements on our website. Google uses cookies to serve ads based on your prior visits to our website or other websites. You may opt out of personalized advertising by visiting <a href="https://www.google.com/settings/ads" target="_blank" rel="noreferrer">Google&#39;s Ads Settings</a>.</p><h3 id="_3-2-third-party-cookies" tabindex="-1">3.2 Third-Party Cookies <a class="header-anchor" href="#_3-2-third-party-cookies" aria-label="Permalink to &quot;3.2 Third-Party Cookies&quot;">​</a></h3><p>Third-party vendors, including Google, use cookies to serve ads based on your past visits to our website. These cookies enable them and their partners to serve ads based on your visit to our site and/or other sites on the Internet.</p><h3 id="_3-3-gdpr-compliance" tabindex="-1">3.3 GDPR Compliance <a class="header-anchor" href="#_3-3-gdpr-compliance" aria-label="Permalink to &quot;3.3 GDPR Compliance&quot;">​</a></h3><p>For users in the European Economic Area (EEA), we comply with GDPR requirements by:</p><ul><li>Obtaining explicit consent before using non-essential cookies</li><li>Providing clear information about data collection practices</li><li>Allowing you to withdraw consent at any time</li><li>Enabling you to access, correct, or delete your personal data</li></ul><h2 id="_4-information-sharing-and-disclosure" tabindex="-1">4. Information Sharing and Disclosure <a class="header-anchor" href="#_4-information-sharing-and-disclosure" aria-label="Permalink to &quot;4. Information Sharing and Disclosure&quot;">​</a></h2><p>We do not sell, trade, or rent your personal information to third parties. We may share information with:</p><ul><li><strong>Service Providers</strong>: Third-party companies that help us operate our website (hosting, analytics, email services)</li><li><strong>Advertising Partners</strong>: Google AdSense and other advertising networks</li><li><strong>Legal Authorities</strong>: When required by law or to protect our rights</li><li><strong>Business Transfers</strong>: In the event of a merger, acquisition, or sale of assets</li></ul><h2 id="_5-data-security" tabindex="-1">5. Data Security <a class="header-anchor" href="#_5-data-security" aria-label="Permalink to &quot;5. Data Security&quot;">​</a></h2><p>We implement appropriate technical and organizational measures to protect your personal information, including:</p><ul><li>Encryption of data in transit (HTTPS)</li><li>Regular security audits</li><li>Access controls and authentication</li><li>Secure data storage</li></ul><p>However, no method of transmission over the Internet is 100% secure. While we strive to protect your information, we cannot guarantee absolute security.</p><h2 id="_6-data-retention" tabindex="-1">6. Data Retention <a class="header-anchor" href="#_6-data-retention" aria-label="Permalink to &quot;6. Data Retention&quot;">​</a></h2><p>We retain your personal information only for as long as necessary to fulfill the purposes outlined in this Privacy Policy, unless a longer retention period is required by law.</p><ul><li><strong>Account Data</strong>: Retained until you request deletion</li><li><strong>Analytics Data</strong>: Aggregated and anonymized after 26 months</li><li><strong>Cookies</strong>: Most expire after 2 years</li></ul><h2 id="_7-your-rights-and-choices" tabindex="-1">7. Your Rights and Choices <a class="header-anchor" href="#_7-your-rights-and-choices" aria-label="Permalink to &quot;7. Your Rights and Choices&quot;">​</a></h2><p>Depending on your location, you may have the following rights:</p><h3 id="_7-1-access-and-correction" tabindex="-1">7.1 Access and Correction <a class="header-anchor" href="#_7-1-access-and-correction" aria-label="Permalink to &quot;7.1 Access and Correction&quot;">​</a></h3><p>You have the right to access and update your personal information.</p><h3 id="_7-2-data-portability" tabindex="-1">7.2 Data Portability <a class="header-anchor" href="#_7-2-data-portability" aria-label="Permalink to &quot;7.2 Data Portability&quot;">​</a></h3><p>You can request a copy of your personal data in a structured, machine-readable format.</p><h3 id="_7-3-deletion" tabindex="-1">7.3 Deletion <a class="header-anchor" href="#_7-3-deletion" aria-label="Permalink to &quot;7.3 Deletion&quot;">​</a></h3><p>You can request deletion of your personal information (right to be forgotten).</p><h3 id="_7-4-opt-out" tabindex="-1">7.4 Opt-Out <a class="header-anchor" href="#_7-4-opt-out" aria-label="Permalink to &quot;7.4 Opt-Out&quot;">​</a></h3><p>You can opt-out of:</p><ul><li>Marketing communications by clicking &quot;unsubscribe&quot; in our emails</li><li>Personalized advertising by visiting <a href="http://www.networkadvertising.org/choices/" target="_blank" rel="noreferrer">NAI Opt-Out</a> or <a href="http://www.aboutads.info/choices/" target="_blank" rel="noreferrer">DAA Opt-Out</a></li><li>Google Analytics by installing the <a href="https://tools.google.com/dlpage/gaoptout" target="_blank" rel="noreferrer">Google Analytics Opt-out Browser Add-on</a></li></ul><h3 id="_7-5-cookie-management" tabindex="-1">7.5 Cookie Management <a class="header-anchor" href="#_7-5-cookie-management" aria-label="Permalink to &quot;7.5 Cookie Management&quot;">​</a></h3><p>You can manage cookies through your browser settings. Note that disabling cookies may affect website functionality.</p><h2 id="_8-children-s-privacy" tabindex="-1">8. Children&#39;s Privacy <a class="header-anchor" href="#_8-children-s-privacy" aria-label="Permalink to &quot;8. Children&#39;s Privacy&quot;">​</a></h2><p>Our website is not intended for children under 13 years of age. We do not knowingly collect personal information from children. If you believe we have collected information from a child, please contact us immediately.</p><h2 id="_9-international-data-transfers" tabindex="-1">9. International Data Transfers <a class="header-anchor" href="#_9-international-data-transfers" aria-label="Permalink to &quot;9. International Data Transfers&quot;">​</a></h2><p>Your information may be transferred to and maintained on servers located outside of your country. We ensure appropriate safeguards are in place to protect your data in accordance with this Privacy Policy.</p><h2 id="_10-california-privacy-rights-ccpa" tabindex="-1">10. California Privacy Rights (CCPA) <a class="header-anchor" href="#_10-california-privacy-rights-ccpa" aria-label="Permalink to &quot;10. California Privacy Rights (CCPA)&quot;">​</a></h2><p>If you are a California resident, you have specific rights regarding your personal information:</p><ul><li>Right to know what personal information is collected</li><li>Right to delete personal information</li><li>Right to opt-out of the sale of personal information (we do not sell personal information)</li><li>Right to non-discrimination for exercising your rights</li></ul><h2 id="_11-changes-to-this-privacy-policy" tabindex="-1">11. Changes to This Privacy Policy <a class="header-anchor" href="#_11-changes-to-this-privacy-policy" aria-label="Permalink to &quot;11. Changes to This Privacy Policy&quot;">​</a></h2><p>We may update this Privacy Policy from time to time. We will notify you of any material changes by:</p><ul><li>Posting the new Privacy Policy on this page</li><li>Updating the &quot;Last Updated&quot; date</li><li>Sending an email notification (for significant changes)</li></ul><p>We encourage you to review this Privacy Policy periodically.</p><h2 id="_12-contact-us" tabindex="-1">12. Contact Us <a class="header-anchor" href="#_12-contact-us" aria-label="Permalink to &quot;12. Contact Us&quot;">​</a></h2><p>If you have any questions, concerns, or requests regarding this Privacy Policy, please contact us:</p><ul><li><strong>Email</strong>: <a href="mailto:lampxiezi@163.com" target="_blank" rel="noreferrer">lampxiezi@163.com</a></li><li><strong>Website</strong>: <a href="https://friday-go.icu" target="_blank" rel="noreferrer">https://friday-go.icu</a></li><li><strong>Response Time</strong>: We aim to respond within 48 hours</li></ul><h2 id="_13-consent" tabindex="-1">13. Consent <a class="header-anchor" href="#_13-consent" aria-label="Permalink to &quot;13. Consent&quot;">​</a></h2><p>By using our website, you consent to this Privacy Policy and agree to its terms.</p><hr><p><strong>Additional Resources:</strong></p><ul><li><a href="https://gdpr.eu/" target="_blank" rel="noreferrer">GDPR Information</a></li><li><a href="https://policies.google.com/privacy" target="_blank" rel="noreferrer">Google Privacy Policy</a></li><li><a href="https://support.google.com/adsense/answer/48182" target="_blank" rel="noreferrer">Google AdSense Policies</a></li></ul>',70)]))}const g=o(r,[["render",n]]);export{p as __pageData,g as default};
