import{_ as e,c as i,o as l,a6 as t}from"./chunks/framework.V1MgpNK7.js";const u=JSON.parse('{"title":"隐私政策","description":"","frontmatter":{"title":"隐私政策","date":"2024-03-14T00:00:00.000Z","author":"PFinal南丞","hidden":true,"head":[["meta",{"name":"keywords","content":"pfinalclub, git, gitsite, javascript, node, jquery, python, php, laravel, sql, database, linux, operating system, os, cpu, verilog, risc-v, bitcoin, ethereum, ai, 教程, 软件, 编程, 开发, 运维, 云计算, 网络, 互联网, 比特币, 以太坊, 操作系统, 智能合约, 数字货币, 爬虫, 逆向"}]]},"headers":[],"relativePath":"zh/privacy-policy.md","filePath":"zh/privacy-policy.md","lastUpdated":1753151274000}'),r={name:"zh/privacy-policy.md"};function o(n,a,h,s,d,c){return l(),i("div",{"data-pagefind-body":!0},a[0]||(a[0]=[t('<h1 id="隐私政策" tabindex="-1">隐私政策 <a class="header-anchor" href="#隐私政策" aria-label="Permalink to &quot;隐私政策&quot;">​</a></h1><h2 id="_1-信息收集" tabindex="-1">1. 信息收集 <a class="header-anchor" href="#_1-信息收集" aria-label="Permalink to &quot;1. 信息收集&quot;">​</a></h2><p>我们收集信息以为用户提供更好的服务。我们收集的信息类型包括：</p><ul><li><strong>使用数据</strong>：关于您如何使用我们网站的信息</li><li><strong>技术数据</strong>：浏览器类型、IP地址、设备信息</li><li><strong>Cookie</strong>：我们使用cookie和类似的跟踪技术</li></ul><h2 id="_2-信息使用" tabindex="-1">2. 信息使用 <a class="header-anchor" href="#_2-信息使用" aria-label="Permalink to &quot;2. 信息使用&quot;">​</a></h2><p>我们使用收集的信息用于：</p><ul><li>提供和维护我们的服务</li><li>改善用户体验</li><li>分析和性能监控</li><li>显示相关广告</li></ul><h2 id="_3-信息共享" tabindex="-1">3. 信息共享 <a class="header-anchor" href="#_3-信息共享" aria-label="Permalink to &quot;3. 信息共享&quot;">​</a></h2><p>我们不会出售或出租您的个人信息。我们可能与以下方共享信息：</p><ul><li>协助我们运营的服务提供商</li><li>法律要求时的执法部门</li><li>分析和广告合作伙伴</li></ul><h2 id="_4-数据安全" tabindex="-1">4. 数据安全 <a class="header-anchor" href="#_4-数据安全" aria-label="Permalink to &quot;4. 数据安全&quot;">​</a></h2><p>我们实施适当的安全措施来保护您的信息。</p><h2 id="_5-您的权利" tabindex="-1">5. 您的权利 <a class="header-anchor" href="#_5-您的权利" aria-label="Permalink to &quot;5. 您的权利&quot;">​</a></h2><p>您有权：</p><ul><li>访问您的个人信息</li><li>请求更正您的数据</li><li>选择退出营销通信</li><li>删除您的账户</li></ul><h2 id="_6-政策更新" tabindex="-1">6. 政策更新 <a class="header-anchor" href="#_6-政策更新" aria-label="Permalink to &quot;6. 政策更新&quot;">​</a></h2><p>我们可能会不时更新此隐私政策。我们会通过在本页面上发布新政策来通知您任何更改。</p><h2 id="_7-联系我们" tabindex="-1">7. 联系我们 <a class="header-anchor" href="#_7-联系我们" aria-label="Permalink to &quot;7. 联系我们&quot;">​</a></h2><p>如果您对本隐私政策有任何问题，请联系我们：</p><p>电子邮件：lampxiezi@163.com</p>',20)]))}const _=e(r,[["render",o]]);export{u as __pageData,_ as default};
